import { useState } from 'react';

export default function Home() {
  const [count, setCount] = useState(0);

  return (
    <main style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', fontFamily: 'Arial, sans-serif' }}>
      <h1>Counter App</h1>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <button onClick={() => setCount(count - 1)} aria-label="Decrease count">-</button>
        <span style={{ fontSize: '2rem', minWidth: '3rem', textAlign: 'center' }}>{count}</span>
        <button onClick={() => setCount(count + 1)} aria-label="Increase count">+</button>
      </div>
    </main>
  );
}
